css:
.page {
  background: #f8fafc;
  color: #0f172a;
  font-family: system-ui, -apple-system, "Segoe UI", sans-serif;
  padding: 48px 0 72px;
}

.container {
  width: min(100% - 32px, 1200px);
  margin: 0 auto;
}

.section-title {
  font-size: clamp(1.9rem, 2.8vw, 2.8rem);
  margin: 0 0 16px;
  letter-spacing: -0.02em;
}

.section-lead {
  font-size: 1.1rem;
  color: #475569;
  margin: 0;
}

.eyebrow {
  font-size: 0.85rem;
  letter-spacing: 0.2em;
  text-transform: uppercase;
  color: #64748b;
  margin: 0;
}

.hero {
  padding: 56px 0;
}

.hero__grid {
  display: grid;
  gap: 32px;
}

.hero__content {
  display: grid;
  gap: 20px;
}

.hero__title {
  font-size: clamp(2.4rem, 4vw, 3.6rem);
  margin: 0;
}

.hero__subtitle {
  font-size: 1.1rem;
  color: #475569;
  margin: 0;
}

.hero__note {
  margin: 0;
  color: #64748b;
}

.hero__card {
  background: #ffffff;
  border: 1px solid #e2e8f0;
  color: #0f172a;
  padding: 28px;
  border-radius: 24px;
  display: grid;
  gap: 16px;
  box-shadow: 0 24px 48px rgba(15, 23, 42, 0.08);
}

.hero__card-title {
  margin: 0;
  font-size: 1.4rem;
}

.hero__list {
  list-style: none;
  padding: 0;
  margin: 0;
  display: grid;
  gap: 12px;
}

.hero__list-item {
  padding-left: 24px;
  position: relative;
}

.hero__list-item::before {
  content: "✓";
  position: absolute;
  left: 0;
  color: #2563eb;
}

.button {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 14px 28px;
  border-radius: 999px;
  font-weight: 600;
  text-decoration: none;
  transition: transform 0.2s ease, box-shadow 0.2s ease;
}

.button--primary {
  background: #2563eb;
  color: #ffffff;
  box-shadow: 0 12px 24px rgba(37, 99, 235, 0.25);
}

.button--primary:hover,
.button--primary:focus-visible {
  transform: translateY(-2px);
  box-shadow: 0 16px 32px rgba(37, 99, 235, 0.35);
}

.pain,
.solution,
.benefits,
.proof {
  padding: 48px 0;
}

.grid {
  display: grid;
  gap: 20px;
}

.grid--3 {
  grid-template-columns: 1fr;
}

.card {
  background: #ffffff;
  border: 1px solid #e2e8f0;
  border-radius: 20px;
  padding: 24px;
  box-shadow: 0 18px 40px rgba(15, 23, 42, 0.06);
}

.card__title {
  margin: 0 0 12px;
  font-size: 1.2rem;
}

.card__text {
  margin: 0;
  color: #475569;
}

.solution__grid {
  display: grid;
  gap: 24px;
}

.checklist {
  list-style: none;
  padding: 0;
  margin: 20px 0 0;
  display: grid;
  gap: 12px;
}

.checklist__item {
  position: relative;
  padding-left: 26px;
  color: #0f172a;
}

.checklist__item::before {
  content: "•";
  position: absolute;
  left: 0;
  color: #2563eb;
  font-size: 1.4rem;
  line-height: 1;
}

.solution__highlight {
  background: linear-gradient(135deg, #2563eb, #38bdf8);
  color: #ffffff;
  border-radius: 24px;
  padding: 28px;
  display: grid;
  gap: 10px;
}

.highlight__label {
  text-transform: uppercase;
  letter-spacing: 0.2em;
  font-size: 0.8rem;
  margin: 0;
}

.highlight__value {
  font-size: 2.4rem;
  margin: 0;
  font-weight: 700;
}

.highlight__text {
  margin: 0;
}

.card--quote {
  border-left: 4px solid #2563eb;
}

.quote {
  margin: 0 0 12px;
  font-size: 1.05rem;
}

.quote__author {
  margin: 0;
  color: #475569;
  font-weight: 600;
}

.stats {
  margin-top: 32px;
  display: grid;
  gap: 16px;
  grid-template-columns: repeat(auto-fit, minmax(160px, 1fr));
}

.stats__item {
  background: #ffffff;
  border-radius: 18px;
  padding: 20px;
  text-align: center;
  border: 1px solid #e2e8f0;
}

.stats__value {
  font-size: 1.8rem;
  font-weight: 700;
  margin: 0;
}

.stats__label {
  margin: 6px 0 0;
  color: #475569;
}

@media (min-width: 768px) {
  .hero__grid {
    grid-template-columns: repeat(2, 1fr);
    align-items: center;
  }

  .grid--3 {
    grid-template-columns: repeat(3, 1fr);
  }

  .solution__grid {
    grid-template-columns: 1.2fr 0.8fr;
    align-items: center;
  }
}
